INSERT INTO `news` (`id`, `title`, `slug`, `author`, `intro`, `full`, `images`, `tag`, `status`, `source`, `cat_id`, `user_id`, `created_at`, `updated_at`) VALUES
(16, 'Siêu phẩm chơi game ASUS ROG Phone 2 sẽ được ra mắt vào tháng 7', 'sieu-pham-choi-game-asus-rog-phone-2-se-duoc-ra-mat-vao-thang-7', 'Mai Chí Công', '<p>Si&ecirc;u phẩm chơi game ASUS ROG Phone 2 sẽ được ra mắt v&agrave;o th&aacute;ng 7</p>\r\n', '<h2>Sau những tin đồn v&agrave;o th&aacute;ng 4 năm ngo&aacute;i th&igrave; mới đ&acirc;y,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-asus-zenfone\" target=\"_blank\" title=\"ASUS\" type=\"ASUS\">ASUS</a>&nbsp;đ&atilde; x&aacute;c nhận rằng ASUS ROG Phone 2 sẽ được ra mắt v&agrave;o th&aacute;ng 7 năm 2019.</h2>\r\n\r\n<p>ASUS vừa qua cũng đ&atilde; c&ocirc;ng bố hợp t&aacute;c với Tencent Games (PUBG) tại Trung Quốc để tối ưu h&oacute;a nội dung tr&ograve; chơi dựa tr&ecirc;n phần cứng mới nhằm mang đến cho người d&ugrave;ng trải nghiệm chơi game tốt nhất c&oacute; thể.</p>\r\n\r\n<p><img alt=\"tencent\" src=\"https://cdn.tgdd.vn/Files/2019/06/06/1171497/tencent_800x370.jpg\" title=\"tencent\" /></p>\r\n\r\n<p>Mặc d&ugrave; chưa c&oacute; bất kỳ th&ocirc;ng tin về th&ocirc;ng số kỹ thuật của ASUS ROG Phone 2, tuy nhi&ecirc;n hi vọng rằng chiếc&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"smartphone\" type=\"smartphone\">smartphone</a>&nbsp;n&agrave;y sẽ sở hữu một m&agrave;n h&igrave;nh được cải tiến, được trang bị chip Snapdragon 855, c&oacute; vi&ecirc;n pin lớn đi k&egrave;m sạc nhanh, c&oacute; hệ thống l&agrave;m m&aacute;t được cải thiện v&agrave; sở hữu RAM khủng 10 đến 12 GB + ROM 512 GB.</p>\r\n\r\n<p><img alt=\"ASUS ROG Phone tháº¿ há» má»i sáº½ ra máº¯t vÃ o quÃ½ 3/2019\" src=\"https://cdn.tgdd.vn/Files/2019/04/08/1159597/rog-phone-1_1280x720-800-resize.jpg\" title=\"minh họa\" /></p>\r\n\r\n<p>Ảnh minh họa</p>\r\n\r\n<p>Dự kiến ASUS ROG Phone 2 sẽ được c&ocirc;ng bố tại một sự kiện ra mắt to&agrave;n cầu kết hợp với một sự kiện ở Trung Quốc.</p>\r\n\r\n<p>Ngo&agrave;i ra th&ecirc;m một b&aacute;o c&aacute;o mới từ Ấn Độ cho biết, ASUS sẽ kh&ocirc;ng c&ograve;n được ph&eacute;p sử dụng từ &quot;Zen&quot; trong sản phẩm của h&atilde;ng do vi phạm thương hiệu. Ph&aacute;n quyết sẽ c&oacute; hiệu lực trong t&aacute;m tuần kể từ b&acirc;y giờ.</p>\r\n\r\n<p>Theo đ&oacute;, Telecare Network ở Ấn Độ đ&atilde; c&aacute;o buộc rằng họ đ&atilde; sử dụng Zen v&agrave; ZenMobile l&agrave;m t&ecirc;n sản phẩm của h&atilde;ng kể từ năm 2008. Telecare Network cũng tuy&ecirc;n bố rằng thương hiệu&nbsp;<a href=\"https://www.thegioididong.com/dtdd-asus-zenfone\" target=\"_blank\" title=\"ZenFone\" type=\"ZenFone\">ZenFone</a>&nbsp;của ASUS sẽ tạo ra sự nhầm lẫn đến từ người ti&ecirc;u d&ugrave;ng.</p>\r\n', '1559843752_assus_800x450.jpg', '', 1, '', 22, 2, '2019-06-06 10:55:52', '2019-06-06 10:55:52'),
(17, 'Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam', 'tren-tay-oppo-reno-10x-zoom-ra-mat-tai-viet-nam', 'Mai Chí Công', '<h1><span style=\"font-size:16px\">Tr&ecirc;n tay OPPO Reno 10x Zoom ra mắt tại Việt Nam: Đẹp - Độc - Lạ</span></h1>\r\n', '<h2><span style=\"font-size:14px\">V&agrave;o ng&agrave;y 6/6,&nbsp;<a href=\"https://www.thegioididong.com/dtdd-oppo\" target=\"_blank\" title=\"OPPO\" type=\"OPPO\">OPPO</a>&nbsp;đ&atilde; ch&iacute;nh thức tr&igrave;nh l&agrave;ng&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-reno-10x-zoom-edition\" target=\"_blank\" title=\"Reno 10x Zoom\" type=\"Reno 10x Zoom\">Reno 10x Zoom</a>. Chiếc smartphone n&agrave;y l&agrave; phi&ecirc;n bản kh&aacute;c của d&ograve;ng Reno, tập trung nhiều hơn với khả năng chụp ảnh zoom 10x v&agrave; một số t&iacute;nh năng độc đ&aacute;o kh&aacute;c.</span></h2>\r\n\r\n<p><span style=\"font-size:14px\"><iframe frameborder=\"0\" height=\"314\" scrolling=\"no\" src=\"https://www.facebook.com/plugins/video.php?href=https%3A%2F%2Fwww.facebook.com%2Fthegioididong.tv2019%2Fvideos%2F440792596751239%2F&amp;show_text=0&amp;width=560&amp;height=400\" width=\"560\"></iframe></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Video tr&ecirc;n tay OPPO Reno 10x Zoom</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Khi nh&igrave;n mới nh&igrave;n v&agrave;o chiếc OPPO Reno 10x Zoom m&igrave;nh thấy c&oacute; vẻ bề ngo&agrave;i kh&aacute; giống với cả&nbsp;<a href=\"https://www.thegioididong.com/dtdd/oppo-reno\" target=\"_blank\" title=\"OPPO Reno\" type=\"OPPO Reno\">OPPO Reno</a>. Thực tế khi đặt hai chiếc điện thoại n&agrave;y cạnh nhau, ch&uacute;ng ta sẽ nhận ra OPPO Reno 10x Zoom sẽ c&oacute; một số điểm kh&aacute;c biệt như sau.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" src=\"https://cdn.tgdd.vn/Files/2019/06/06/1171422/62026394_1106013549606596_921925360130457600_n_800x450.jpg\" title=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Reno 10x Zoom b&ecirc;n tr&aacute;i c&oacute; k&iacute;ch thước m&agrave;n h&igrave;nh lớn hơn hẳn so với Reno.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" src=\"https://cdn.tgdd.vn/Files/2019/06/06/1171422/62035196_397938544137991_5783013781474902016_n_800x450.jpg\" title=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ph&iacute;a mặt lưng, Reno 10x Zoom b&ecirc;n phải c&oacute; th&ecirc;m một camera chuy&ecirc;n dụng để zoom 10x. Như vậy phi&ecirc;n bản Reno 10x Zoom sẽ c&oacute; tới 3 camera so với Reno chỉ c&oacute; 2 camera.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" src=\"https://cdn.tgdd.vn/Files/2019/06/06/1171422/61986992_493897201401415_6146681444249894912_n_800x450.jpg\" title=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Ph&iacute;a mặt đ&aacute;y, Reno 10x Zoom đ&atilde; lược bỏ đi cổng &acirc;m thanh 3.5 mm v&agrave; thay thế chi vị tr&iacute; đ&oacute; l&agrave; khe cắm SIM, thẻ nhớ.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" src=\"https://cdn.tgdd.vn/Files/2019/06/06/1171422/61972378_1205849196241429_826439694450425856_n_800x450.jpg\" title=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Tuy c&oacute; một ch&uacute;t kh&aacute;c biệt l&agrave; vậy nhưng cả hai chiếc OPPO Reno bản Zoom 10x v&agrave; bản thường đều sở hữu cho m&igrave;nh camera selfie v&acirc;y c&aacute; mập.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" src=\"https://cdn.tgdd.vn/Files/2019/06/06/1171422/62227712_370698710240386_4184926127887745024_n_800x450.jpg\" title=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">B&acirc;y giờ l&agrave; l&uacute;c ch&uacute;ng ta sẽ đi chi tiết hơn về chiếc OPPO Reno phi&ecirc;n bản Zoom 10x n&agrave;y. M&aacute;y sử dụng c&ocirc;ng nghệ m&agrave;n h&igrave;nh to&agrave;n cảnh với t&ecirc;n gọi Panoramic v&agrave; đ&acirc;y l&agrave; m&agrave;n h&igrave;nh AMOLED với k&iacute;ch thước l&ecirc;n tới 6.6 inch, độ ph&acirc;n giải Full HD+.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" src=\"https://cdn.tgdd.vn/Files/2019/06/06/1171422/61839569_1088242228049731_6452895243606949888_n_800x450.jpg\" title=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Nếu để &yacute; kỹ hơn ở ph&iacute;a dưới, chiếc &quot;cằm&quot; của m&aacute;y đ&atilde; được gọt mỏng đi hơn rất nhiều so với nhiều chiếc smartphone c&oacute; tr&ecirc;n thị trường.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" src=\"https://cdn.tgdd.vn/Files/2019/06/06/1171422/62144321_2457250787666236_3998988008572321792_n_800x450.jpg\" title=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">M&igrave;nh c&oacute; cảm gi&aacute;c rằng mặt lưng của Reno 10x Zoom c&oacute; khả năng đổi m&agrave;u theo g&oacute;c nh&igrave;n, khi chụp từ một g&oacute;c kh&aacute;c, mặt lưng m&aacute;y cho m&agrave;u t&iacute;m như thế n&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" src=\"https://cdn.tgdd.vn/Files/2019/06/06/1171422/61885732_372322856726824_3368246119552253952_n_800x450.jpg\" title=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cụm ba camera tr&ecirc;n m&aacute;y c&oacute; những th&ocirc;ng số v&agrave; t&iacute;nh năng như sau:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-size:14px\">Camera ch&iacute;nh: độ ph&acirc;n giải 48 MP, khẩu độ f/1.7, ti&ecirc;u cự 26 mm, lấy n&eacute;t theo pha v&agrave; lấy n&eacute;t bằng Laser, c&oacute; c&ocirc;ng nghệ chống rung quang học OIS.</span></li>\r\n	<li><span style=\"font-size:14px\">Camera g&oacute;c si&ecirc;u rộng: độ ph&acirc;n giải 8 MP, khẩu độ f/2.2, ti&ecirc;u cự 16 mm.</span></li>\r\n	<li><span style=\"font-size:14px\">Camera tiềm vọng (ống k&iacute;nh ph&iacute;a dưới c&ugrave;ng): độ ph&acirc;n giải 13 MP, khẩu độ f/3.0, ti&ecirc;u cự 130 mm, zoom quang học 5x, zoom kỹ thuật số l&ecirc;n tới 60x, lấy n&eacute;t theo pha v&agrave; lấy n&eacute;t bằng Laser, c&oacute; c&ocirc;ng nghệ chống rung quang học OIS.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" src=\"https://cdn.tgdd.vn/Files/2019/06/06/1171422/61890264_2352348391666673_252076432375676928_n_800x450.jpg\" title=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Camera selfie của m&aacute;y c&oacute; th&ocirc;ng số tương tự như tr&ecirc;n chiếc Reno với độ ph&acirc;n giải 16 MP, khẩu độ f/2.0 v&agrave; ti&ecirc;u cự 26 mm.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" src=\"https://cdn.tgdd.vn/Files/2019/06/06/1171422/62132208_2391776377720732_3079218283937792000_n_800x450.jpg\" title=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">X&eacute;t về cấu h&igrave;nh thi phi&ecirc;n bản Reno 10x Zoom mang cấu h&igrave;nh &quot;khủng&quot; nhất hiện nay với chip Snapdragon 855, RAM 8 GB v&agrave; bộ nhớ trong 256 GB. Chắc chắn rằng với phần cứng n&agrave;y việc chơi game sẽ đ&atilde; hơn bao giờ hết.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" src=\"https://cdn.tgdd.vn/Files/2019/06/06/1171422/61970584_2287153214710910_6146318927535276032_n_800x450.jpg\" title=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">Cuối c&ugrave;ng, m&aacute;y sở hữu vi&ecirc;n pin dung lượng 4065 mAh (lớn hơn so với phi&ecirc;n bản Reno thường) v&agrave; đi k&egrave;m với c&ocirc;ng nghệ sạc nhanh VOOC 3.0 sẽ gi&uacute;p cho bạn thoải m&aacute;i sử dụng m&agrave; chẳng sợ hết pin.</span></p>\r\n\r\n<p><span style=\"font-size:14px\">Gi&aacute; b&aacute;n ch&iacute;nh thức của Reno 10x Zoom tại Việt Nam l&agrave; 20.990.000. Với mức gi&aacute; n&agrave;y, đ&acirc;y c&oacute; thể coi l&agrave; chiếc flagship đầu bảng của OPPO.</span></p>\r\n\r\n<p><span style=\"font-size:14px\"><img alt=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" src=\"https://cdn.tgdd.vn/Files/2019/06/06/1171422/61796340_403556877166350_1857153217524137984_n_800x450.jpg\" title=\"Trên tay OPPO Reno 10x Zoom ra mắt tại Việt Nam\" /></span></p>\r\n\r\n<p><span style=\"font-size:14px\">C&aacute;c bạn c&ograve;n muốn ch&uacute;ng m&igrave;nh thực hiện th&ecirc;m những b&agrave;i đ&aacute;nh gi&aacute; chi tiết n&agrave;o với OPPO Reno 10x Zoom nữa, h&atilde;y để lại &yacute; kiến b&ecirc;n dưới nh&eacute;.</span></p>\r\n', '1559844043_oppo reno.jpg', '', 1, '', 22, 2, '2019-06-06 11:01:42', '2019-06-06 11:01:42');

